mapper.delete:
  		/*회원 탈퇴*/
  		delete
		from
			members
		where
			mbr_id = #{memberId};

  		/*특정 회원 로그인 이력 삭제*/
  		delete
		from 
			members_login_log
		where
			login_id = #{memberId};

mapper.insert:
  		/*회원등록*/
  		INSERT INTO members
		(mbr_id, mbr_pw, mbr_name, mbr_grd, mbr_addr, mbr_daddr, mbr_zip, mbr_telno, mbr_email, mbr_reg_date)
		VALUES
		(#{memberId}, #{memberPw}, #{memberName}, #{memberLevel}, #{memberAddress}, #{memberDAddress}, ${memberZip}, #{memberTelno}, #{memberEmail}, CURDATE());
